import React, { Component } from "react";
import { AddDepModal } from "./AddDepModal";
import { Button, ButtonToolbar, Table } from "react-bootstrap";
import { EditDepModal } from "./EditDepModal";

export class Employee extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emps: [],
      addModalShow: false,
      editModalShow: false,
    };
  }

  componentDidMount() {
    this.refreshlist();
  }
  componentDidUpdate() {
    this.refreshlist();
  }
  refreshlist() {
    fetch("http://localhost:52396/api/employee")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        this.setState({
          emps: data,
        });
      });
  }
  deleteEmp(empid) {
    if (window.confirm(`Are you sure?`)) {
      fetch("http://localhost:52396/api/employee/" + empid, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      });
    }
  }

  render() {
    const { emps, empid, empname } = this.state;
    let addModalClose = () => {
      this.setState({ addModalShow: false });
    };
    let editModalClose = () => {
      this.setState({ editModalShow: false });
    };
    return (
      <div>
        <Table className="mt-4" striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Employee Name</th>
              <th>Department</th>
              <th>Mail ID</th>
              <th>Date of Join</th>
              <th>Options</th>
            </tr>
          </thead>
          <tbody>
            {emps.map((emp) => (
              <tr key={emp.emp}>
                <td>{emp.EmployeeID}</td>
                <td>{emp.EmployeeName}</td>
                <td>{emp.Department}</td>
                <td>{emp.MailID}</td>
                <td>{emp.DOJ}</td>
                <td>
                  <ButtonToolbar>
                    <Button
                      variant="warning"
                      onClick={() => {
                        this.setState({
                          editModalShow: true,
                          empid: emp.EmployeeID,
                          empname: emp.EmployeeName,
                        });
                      }}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="danger"
                      style={{ marginLeft: "10px" }}
                      onClick={() => this.deleteEmp(emp.EmployeeID)}
                    >
                      Delete
                    </Button>

                    <EditDepModal
                      show={this.state.editModalShow}
                      onHide={editModalClose}
                      empid={empid}
                      empname={empname}
                    />
                  </ButtonToolbar>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <ButtonToolbar>
          <Button
            onClick={() =>
              this.setState({
                addModalShow: true,
              })
            }
            variant="success"
          >
            Add Department
          </Button>
        </ButtonToolbar>
        <AddDepModal show={this.state.addModalShow} onHide={addModalClose} />
      </div>
    );
  }
}
