import React, { Component } from "react";
import { Table } from "react-bootstrap";
import { AddDepModal } from "./AddDepModal";
import { Button,ButtonToolbar } from "react-bootstrap";
import { EditDepModal } from "./EditDepModal";

export class Department extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deps: [],
      addModalShow:false,
      editModalShow:false
    };
  }

  componentDidMount() {
    this.refreshlist();
  }
  componentDidUpdate() {
    this.refreshlist();
  }

  refreshlist() {
    fetch("http://localhost:52396/api/Department").then((response) => {
      return response.json();
    }).then((data)=>{
        this.setState({
            deps:data
        })
    })
  }

  render() {
    const { deps,depid,depname } = this.state;
    let addModalClose= ()=>{
        this.setState({addModalShow:false})
    }
    return (
        <div>
      <Table className="mt-4" striped bordered hover size="sm">
        <thead>
          <tr>
            <th>Department ID</th>
            <th>Department Name</th>
            <th>Option</th>
          </tr>
        </thead>
        <tbody>
          {deps.map((dep) => (
            <tr key={dep.DepartmentID}>
              <td>{dep.DepartmentID}</td>
              <td>{dep.DepartmentName}</td>
              <td>

<ButtonToolbar>
  <Button onClick={()=>{
    this.setState({editModalShow:true,depid:dep.DepartmentID,depname:dep.DepartmentName})
  }}>
    Edit
  </Button>

  <EditDepModal
  show={this.state.editModalShow}
  />
</ButtonToolbar>

              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <ButtonToolbar>
<Button onClick={()=> this.setState({
    addModalShow:true
})} variant="success">Add Department</Button>
      </ButtonToolbar>
      <AddDepModal show={this.state.addModalShow} onHide={addModalClose}/>
      </div>
    );
  }
}
