import React, { Component } from 'react'
import { Modal,Button,Row,Col,Form, FormGroup, FormLabel, FormControl } from 'react-bootstrap'


export class AddDepModal extends Component {
  handleSubmit(e){
    e.preventDefault();
    fetch("http://localhost:52396/api/department",{method:'POST',
      headers:{
        'Accept':'application/json',
        'Content-Type':'application/json'
      },
      body:JSON.stringify({
        DepartmentID:null,
        DepartmentName: e.target.DepartmentName.value
      }).then(res=>res.JSON()).then((result)=>{
        alert(result)
      },(error)=>{
        alert("Failed");
      })
    })
  }
  render() {
    return (
        <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add Department
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className='container'>
            <Row>
              <Col sm={6}>
              <Form onSubmit={this.handleSubmit}>
                <FormGroup>
                  <FormLabel>
                    Department Name
                  </FormLabel>
                  <FormControl type='text' name="DepartmentName" required placeholder='Department Name'/>
                </FormGroup>

                <FormGroup>
                  <Button variant='primary' type="submit" className='mt-2'>Add Department</Button>
                </FormGroup>

              </Form>
              </Col>
            </Row>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='danger' onClick={this.props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    )
  }
}
